import {Header} from './components/header';
import {Footer} from './components/footer';
import {useState} from 'react';
const App = () => {


  const [name, setName] = useState('Demetrio');
  const [bg, setBg] = useState('#FF0000');
  const [age, setAge] = useState(90);


  const [list, setList] = useState([
    "O rato roeu a roupa do rei de roma",
    "Aqui vai uma frase poética",
    "Se você não fizer, ninguém vai fazer para você."
  ]); 


  const handle20 = () => {
    setAge(20);
    setBg("#FF0000")
  }

  const handle90 = () => {
    setAge(90);
    setBg("#00FF00")
  }


  return (
    <div style={{backgroundColor: bg}}>
      <Header name={name} age={age}/>

      {age === 90 && 
        <button onClick={handle20}>Tenho 20 anos</button>
      }

      {age === 20 &&
      <button onClick={handle90}>Tenho 90 anos</button>
      }

      <hr/>

      <ul>
        {list.map((item, index) =>(
          <li key={index}>{item}</li>
        ) )}
      </ul>

      <Footer/>
    </div>
  )
}


export default App;
